import { useEffect, useState } from 'react';
import { MenuItem, FormControl, Select, CardContent, Card } from '@material-ui/core';
import './App.css';
import Map from './Map';
import InfoBox from './infoBox';
function App() {

  const [countries, setCountries] = useState([]);
  const [country, setCountry] = useState(["Worldwide"]);
  const [countryInfo, setCountryInfo] = useState({});
  //STATE = how to write a variable in React

  //USEEFFECT = runs a piece of code based on a given condition

  useEffect(() => {
    //async -> send a request, wait for it, do something with info

    const getCountriesData = async() => {
      await fetch("https://disease.sh/v3/covid-19/countries")
        .then((response) => response.json())
        .then((data) => {
          const countries = data.map((country) => ({
            name: country.country,
            value: country.countryInfo.iso2
          }));
          setCountries(countries);
        });
    };
    getCountriesData();
  }, []);

  const onCountryChange = async(event) => {
    const countryCode = event.target.value;

    setCountry(countryCode);

    const url = 
    countryCode === "worldwide" ? "https://disease.sh/v3/covid-19/countries/all" : 'https://disease.sh/v3/covid-19/countries/${countryCode}'

    await fetch(url)
    .then(response => response.json())
    .then(data => {
        setCountry(countryCode);
        setCountryInfo(data);
    })
  }
  return (
    <div className="app">
      <div className = "app__left">
        <div className="app__header">
            <h1>COVID19 TRACKER</h1>
            <FormControl className = "app_dropdown">
              <Select variant = "outlined" onChange = {onCountryChange} value = {country}>
                <MenuItem value = "worldwide"> Worldwide </MenuItem>
                {countries.map((country) => (
                  <MenuItem value = {country.value}>{country.name} </MenuItem>
                ))}
              </Select>
            </FormControl>
          </div>

          <div className = "app__stats">
            <InfoBox title = "Coronavirus Cases" cases = {countryInfo.todayCases} total ={2000}/> 
            <InfoBox title = "Recovered" cases = {countryInfo.todayRecovered} total = {3000} />
            <InfoBox title = "Deaths" cases = {countryInfo.todayDeaths} total = {4000} />
          </div>

          <Map />
        </div>

      <Card className = "app__right">
        <CardContent>
           {/*table */}

           <h3>Live Cases by Country</h3>
           {/*Graph */}

           <h3>Worldwide new cases</h3>
        </CardContent>
      </Card>
      </div>
  );
}
{/* Header */}

{/* Title + Select input dropdown field */}

{/* InfoBoxs */}
{/* InfoBoxs */}
{/* InfoBoxs */}

{/* Table */}
{/* Graph */}

{/* Map */}
export default App;
